import React, { useEffect, useState } from 'react';
import { Box, Button } from '@mui/material';
import Form from 'react-bootstrap/Form';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';

const Voice = () => {
  const [voices, setVoices] = useState([]);
  const [selectedVoice, setSelectedVoice] = useState(null);

  useEffect(() => {
    window.speechSynthesis.onvoiceschanged = () => {
      const availableVoices = window.speechSynthesis.getVoices();
      setVoices(availableVoices);
      setSelectedVoice(availableVoices[0]);
    };
  }, []);

  const handleListen = () => {
    const text = document.querySelector("#text").value;
    const speech = new SpeechSynthesisUtterance(text);
    speech.voice = selectedVoice;
    window.speechSynthesis.speak(speech);
  };

  const handleVoiceChange = (event) => {
    const voiceIndex = event.target.value;
    const selectedVoice = voices[voiceIndex];
    setSelectedVoice(selectedVoice);
  };

  return (
    <Box className='voice--back'>
      <h1>Text To Speech Converter</h1>
      <textarea id="text" cols="100" rows="10" placeholder='Write anything here...'></textarea>

      <Box style={{ display: 'flex', alignItems: 'center', gap: '10px', marginTop: '1rem' }}>
        <select id="selct" onChange={handleVoiceChange}>
          {voices.map((voice, index) => (
            <option key={index} value={index}>
              {voice.name}
            </option>
          ))}
        </select>

        <Button variant="contained" color='secondary' onClick={handleListen}><PlayArrowIcon /> Listen</Button>
      </Box>
    </Box>
  );
};

export default Voice;
