import React, { useEffect } from 'react';
import { Box, Button } from '@mui/material';
import Form from 'react-bootstrap/Form';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';

const Voice = () => {
  let speech = new SpeechSynthesisUtterance();
  let voices = [];

  useEffect(() => {
    window.speechSynthesis.onvoiceschanged = () => {
      voices = window.speechSynthesis.getVoices();
      speech.voice = voices[0];
      voices.forEach((voice, index) => (document.getElementById('selct').options[index] = new Option(voice.name, index)));
    };
  }, []);

  const handleListen = () => {
    speech.text = document.querySelector("#text").value;
    window.speechSynthesis.speak(speech);
  };

  return (
    <Box className='voice--back'>
      <h1>Text To Speech Converter</h1>
      <textarea id="text" cols="100" rows="10" placeholder='Write anything here...'></textarea>

      <Box style={{ display: 'flex', alignItems: 'center', gap: '10px', marginTop: '1rem' }}>
        <select id="selct"></select>

        <Button variant="contained" color='secondary' onClick={handleListen}><PlayArrowIcon /> Listen</Button>
      </Box>
    </Box>
  );
};

export default Voice;
